def longestPalindrome(s: str) -> str:
    max_len = 0
    max_word = ''
    
    for i in range(len(s)):
        for j in range(i, len(s)):
            if s[i:j+1] == "".join(reversed(s[i:j+1])):
                if len(s[i:j+1]) > max_len:
                    max_word = s[i:j+1]
                    max_len = len(s[i:j+1])
    return max_word

            
    
